var RestUtil = require('oae-rest/lib/util');

/**
 * Import a collection of departments from a CSV-file
 *
 * @param  {Context}        restCtx                 Standard REST Context object that contains the current tenant URL and the current user credentials
 * @param  {Object}         opts                    Object that contains the request parameters
 * @param  {String}         opts.tenantAlias        The alias of the tenant where the departments are imported. Note that this will only be used when importing departments on the global admin
 * @param  {Function}       opts.departments        A function that returns a stream which points to a CSV file containing the departments
 * @param  {Function}       callback                Standard callback function
 * @param  {Object}         callback.err            Standard error object, if any
 */
var importDepartments = module.exports.importDepartments = function(restCtx, opts, callback) {
    RestUtil.RestRequest(restCtx, '/api/departments/import', 'POST', opts, callback);
};
