var RestUtil = require('oae-rest/lib/util');

/**
 * Import a collection of journals from a CSV-file
 *
 * @param  {Context}        restCtx                 Standard REST Context object that contains the current tenant URL and the current user credentials
 * @param  {Object}         journalsCSV             File object representing the uploaded CSV file as returned by Express
 * @param  {Function}       callback                Standard callback function
 * @param  {Object}         callback.err            Standard error object, if any
 */
var importJournals = module.exports.importJournals = function(restCtx, journalsCSV, callback) {
    RestUtil.RestRequest(restCtx, '/api/journals/import', 'POST', journalsCSV, callback);
};
