var _ = require('underscore');
var OAE = require('oae-util/lib/oae');

var MixPanelAPI = require('oae-mixpanel/lib/api');

/**
 * @REST getUniqueUsers
 *
 * Get the unique user count stats for all tenants for the current month
 *
 * @Server      admin
 * @Method      GET
 * @Path        /mixpanel/stats/uniqueUsers
 * @Return      {Object}                        Object containing the unique users for all tenants
 * @HttpResponse                    200         The unique users for all tenants
 * @HttpResponse                    401         Must be a global administrator user to view the stats
 */
OAE.globalAdminRouter.on('get', '/api/mixpanel/stats/uniqueUsers', function(req, res) {
    MixPanelAPI.getUniqueUsers(req.ctx, function(err, data) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        return res.status(200).send(data);
    });
});

/**
 * @REST getUniqueUsersForTenant
 *
 * Get the unique user count stats for a specific tenant for the current month
 *
 * @Server      admin
 * @Method      GET
 * @Path        /mixpanel/stats/uniqueUsers/{tenantAlias}
 * @Return      {Object}                        Object containing the unique users for a specific tenant
 * @HttpResponse                    200         The unique users for a specific tenant
 * @HttpResponse                    400         Missing tenant alias
 * @HttpResponse                    401         Must be a global administrator user to view the stats
 */
OAE.globalAdminRouter.on('get', '/api/mixpanel/stats/uniqueUsers/:tenantAlias', function(req, res) {
    MixPanelAPI.getUniqueUsersForTenant(req.ctx, req.params.tenantAlias, function(err, data) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        return res.status(200).send(data);
    });
});

/**
 * @REST getUniqueUsersForTenant
 *
 * Get the unique user count stats for the current tenant for the current month
 *
 * @Server      app
 * @Method      GET
 * @Path        /mixpanel/stats/uniqueUsers
 * @Return      {Object}                        Object containing the unique users for the current tenant
 * @HttpResponse                    200         The unique users for the current tenant
 * @HttpResponse                    401         Must be an administrator user to view the stats
 */
OAE.tenantRouter.on('get', '/api/mixpanel/stats/uniqueUsers', function(req, res) {
    MixPanelAPI.getUniqueUsers(req.ctx, function(err, data) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        return res.status(200).send(data);
    });
});
