/**
 * Intercept HTTP requests to the MixPanel API
 *
 * @param  {Object}     response    The response from the mocked API
 */
var mockRequests = module.exports.mockRequests = function(response) {
    response = response || {'status': 'OK'};

    var nock = require('nock');

    // Ensure we can still perform regular HTTP requests during the tests
    nock.enableNetConnect();

    // Intercept the HTTP request
    nock('https://mixpanel.com')
        .filteringPath(function(path) {
            return '/';
        })
        .get('/')
        .reply(200, response);
};
