var OAE = require('oae-util/lib/oae');
var TicketsAPI = require('./api');

////////////
// Global //
////////////

/*!
 * Creates a ticket
 *
 * Rest Endpoint:
 *
 * _POST_ `/api/ticket/:ticketId/create`
 *
 * * returns a JSON object of a ticket
 */
OAE.globalAdminRouter.on('post', '/api/ticket/create', function(req, res) {
    TicketsAPI.createTicket(req.ctx, req.body.tenantAlias, req.body.publicationId, function(err, ticket) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, ticket);
    });
});

/*!
 * Returns a specified ticket
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/ticket/:ticketId`
 *
 * * returns a JSON object of a ticket
 */
OAE.globalAdminRouter.on('get', '/api/ticket/:ticketId', function(req, res) {
    TicketsAPI.getTicket(req.ctx, req.params.ticketId, function(err, ticket) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, ticket);
    });
});

/*!
 * Returns a collection of tickets
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/tickets`
 *
 * * returns a array of JSON objects of tickets
 */
OAE.globalAdminRouter.on('get', '/api/tickets', function(req, res) {
    TicketsAPI.getTickets(req.ctx, req.body.ticketIds, function(err, tickets) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, tickets);
    });
});

